let array = [
    5,
    8,
    3,
    6,
    2,
    9,
    1,
];

var ctx = document.getElementById('myChart').getContext('2d');
var coll = document.getElementsByClassName("random");
var i;

for (i = 0; i < coll.length; i++) {
    coll[i].addEventListener("click", function() {
        this.classList.toggle("actie");
        array.splice(0, 1);
        array.push(Math.round(Math.random() * 10));
        var myChart = new Chart(ctx, {
            type: 'line',
            data: {
                labels: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'],
                datasets: [{
                    label: 'BPM',
                    data: array,
                    backgroundColor: [
                        'rgba(4, 99, 132, 0.2)',
                    ],
                    borderColor: [
                        'rgba(4, 99, 132, 0.2)',
                    ],
                    borderWidth: 1
                }]
            },
            options: {
                scales : {
                    yAxes: [{
                        ticks: {
                            beginAtZero: false
                        }
                    }]
                }
            }
        });
    
        console.log(array);
});
}
